00074 //fonction pour ajouter piece dans la matrice (si manque de place => on supprime le M initial pour éviter un M seul)
00075 void ajouterpiece(CMat &Mat, const Form &forme, int x, int y) {
00076     // Ajouter la pièce à partir de la position (x, y) dans la matrice cible
00077     for (size_t i = 0; i < forme.dim.first; ++i)
00078     {
00079         for (size_t j = 0; j < forme.dim.second; ++j)
00080         {
00081             // Vérifier que nous sommes dans les limites de la matrice cible
00082             if (x + i < Mat.size() && y + j < Mat[0].size())
00083             {
00084                 // Ajouter la pièce uniquement si ce n'est pas un espace vide
00085                 if (forme.piece[i][j] == 'M') {
00086                     Mat[x + i][y + j] = forme.piece[i][j];
00087                 }
00088             }
00089             else
00090             {//sinon on supprime le M initial
00091                 Mat[x][y] = ' ';
00092             }
00093         }
00094     }
00095 }
