00097 void rotation90(Form &forme) {
00098     // Créer une nouvelle matrice avec les dimensions inversées
00099     vector<vector<char>> matrice;
00100     matrice.resize(forme.dim.second, vector<char>(forme.dim.first));
00101 
00102     // Effectuer la rotation de 90° (sens horaire)
00103     for (size_t i = 0; i < forme.dim.first; ++i) {      // Parcours des lignes de l'ancienne matrice
00104         for (size_t j = 0; j < forme.dim.second; ++j) { // Parcours des colonnes de l'ancienne matrice
00105             matrice[j][forme.dim.first - 1 - i] = forme.piece[i][j];
00106         }
00107     }
00108     forme.piece = matrice; // Remplacer la matrice actuelle par la matrice pivotée
00109     swap(forme.dim.first, forme.dim.second); // Mettre à jour les dimensions de la forme
00110 }
