00355             int distance = abs(int(PosMonster.first - test.first)) + abs(int(PosMonster.second - test.second));
00356             if (distance < minDistance) {
00357                 minDistance = distance;
00358                 posplay = test;
00359             }
00360         }
00361 
00362         //regarder si le jouer est pres du montre et s'il n'est dans une safe zone
00363         //pour activer le pathfinding et suivre le bot
00364         if (size_t(minDistance) < (param.NbColumn + param.NbRow) / 3 &&
00365             posplay.second > 2 && posplay.first > 2 &&
00366             Mat.size() - posplay.first > 2 && Mat[0].size() - posplay.second > 2){
00367 
